import React, { useState } from 'react';
import './ExpenseForm.css';
const ExpenseForm = () => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enterAmount, setEnterAmount] = useState('');
    const [enterDate, setEnterDate] = useState('');
    // const [userInput, setUserInput] = useState({
    //     enteredTitle: '',
    //     enterAmount: '',
    //     enteredDate: ''
    // });
    const titleChangeHandler = (e) => {
        setEnteredTitle(e.target.value);
        // setUserInput(
        //     {
        //         ...userInput,
        //         enteredTitle: e.target.value,
        //     }
        // )
        // setUserInput((prevState)=>{
        //     return {...prevState,enteredTitle: e.target.value}
        // });
    };
    const acountChangeHandler = (e) => {
        setEnterAmount(e.target.value);
        // setUserInput({
        //     ...userInput,
        //     enterAmount: e.target.value;
        // });
    };
    const dateChangeHandler = (e) => {
        setEnterDate(e.target.value);
        // setUserInput({
        //     ...userInput,
        //     enteredDate: e.target.value,
        // });
    };

    const submissionHandler = (event) => {
        event.preventDefault();
        const expenseDate = {
            title: enteredTitle,
            amount: enterAmount,
            date: new Date(enterDate)
        };
        console.log(expenseDate);
        setEnteredTitle('');
        setEnterAmount('');
        setEnterDate('');
    };

    return (<form onSubmit={submissionHandler}>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type="text" value={enteredTitle} onChange={titleChangeHandler} />
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" value={enterAmount} min="0.01" step="0.01" onChange={acountChangeHandler} />
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" value={enterDate} min="2000-01-01" max="2026-01-01" onChange={dateChangeHandler} />
            </div>
        </div>
        <div className="new-expense__actions">
            <button type="submit">Add Expense</button>
        </div>
    </form>);
};

export default ExpenseForm;
